package app.array.no15_3sum;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.HashSet;
import java.util.List;
import java.util.Map;
import java.util.Set;

class Solution {
    // approach 2: no hash
    public List<List<Integer>> threeSum(int[] nums) {
        // sort
        Arrays.sort(nums);
        List<List<Integer>> res = new ArrayList<>();

        for (int i = 0; i < nums.length - 2; i++) {
            if (i == 0 || nums[i] != nums[i - 1]) {
                int first = nums[i];
                int lo = i + 1, hi = nums.length - 1, sumOfLohi = 0 - nums[i];
                while (lo < hi) {
                    int actual = nums[lo] + nums[hi];
                    if (sumOfLohi == actual) {
                        List<Integer> l = Arrays.asList(new Integer[] { first, nums[lo], nums[hi] });
                        // add one result
                        res.add(l);
                        // skip duplicate part
                        while (lo < hi && nums[lo] == nums[lo + 1]) lo++;
                        while (lo < hi && nums[hi] == nums[hi - 1]) hi--;
                        
                        lo++; 
                        hi--;
                    } else if (sumOfLohi < actual) hi--;
                    else lo++;
                }
            }

        }

        return res;
    }

    // approach 1
    public List<List<Integer>> threeSum1(int[] nums) {
        // !! sort first
        Arrays.sort(nums);
        // !! use set and sort to remove duplicate
        Set<List<Integer>> res = new HashSet<>();
        Map<Integer, Integer> cache = new HashMap<>();
        // hash all element
        for (int num : nums) {
            cache.put(num, cache.getOrDefault(num, 0) + 1);
        }

        // O(n^2)
        for (int i = 0; i < nums.length; i++) {
            for (int j = i + 1; j < nums.length; j++) {
                int complement = 0 - nums[i] - nums[j];
                if (cache.containsKey(complement)) {
                    // if the complement number is not same with nums[i] and nums[j]
                    if (complement != nums[i] && complement != nums[j]) {
                        addRes(res, nums, i, j, complement);
                    } else {
                        // if the complement number is same with one of the both, then
                        // this number must have 2 or more in the array
                        int num = cache.get(complement);
                        if (complement != nums[i] || complement != nums[j]) {
                            if (num >= 2) {
                                addRes(res, nums, i, j, complement);
                            }
                        } else {
                            // if the complement number is same with the both, then
                            // this number must have 3 or more in the array
                            if (num > 2) {
                                addRes(res, nums, i, j, complement);
                            }
                        }
                    } // end else
                }
            }
        }


        return new ArrayList<>(res);
    }

    public void addRes(Set<List<Integer>> res, int[] nums, int i, int j, int complement) {
        List<Integer> l = new ArrayList<>();
        if (nums[j] < complement) {
            l.add(nums[i]);
            l.add(nums[j]);
            l.add(complement);
        } else if (nums[i] < complement){
            l.add(nums[i]);
            l.add(complement);
            l.add(nums[j]);
        } else {
            l.add(complement);
            l.add(nums[i]);
            l.add(nums[j]);
        }
        res.add(l);
    }
}


